mapper.insert:
insert into account
    (userCode,
    userName,
    gender,
    birthday,
    IDcard,
    offlineTime,
    userType,
    headImgUrl,
    time)
values (#{userCode}
    ,#{userName}
    ,#{gender}
    ,#{birthday}
    ,#{IDcard}
    ,#{offlineTime}
    ,#{userType}
    ,#{headImgUrl}
    ,#{time})
