mapper.select:
select count(id) from singleevent


        userId = #{userid}


        and month = #{month}


        and day = #{day}


        and year = #{year}


and ((startTime > #{starttime} and endTime < #{endtime})
or
(startTime > #{starttime} and startTime < #{endtime})
or
(endTime > #{starttime} and endTime < #{endtime})
or
(startTime <= #{starttime} and endTime >= #{endtime}))

select

from singleevent


        userid = #{userid}


        and day = #{day}


        and month = #{month}


        and year = #{year}

    and isOverdue in (1,0) and isLoop = 0


order by day asc ,startTime asc

select

from singleevent


        userid = #{userid}


        and day = #{day}


        and month = #{month}


        and year = #{year}

    and isOverdue in (1,0)

order by level asc

select

from singleevent


        userid = #{userid}


        and day = #{day}


        and month = #{month}


        and year = #{year}

    and isOverdue in (1,0)

order by level asc , startTime asc

select day from singleevent where
userId = #{userid}
and month = #{month}
and year = #{year}
group by day

select startTime,endTime from singleevent


        isLoop = #{isLoop}


 and userid=#{userid} and eventid = #{eventid}

select eventId,userId,eventName,day,month,year
from singleevent
where userId = #{userId}
and isOverdue = #{isOverdue}
order by year desc,month desc,day desc limit #{pageNum},#{pageSize}

select eventId,userId,eventName,person,day,month,year
from singleevent

    1 = 1

        and eventName like "%"#{eventname}"%"


        and type = #{type}


        and level = #{level}


        and year = #{year}


        and month = #{month}


        and day = #{day}


        and isOverdue = #{isOverdue}


        and ((startTime >= #{starttime} and startTime <= #{endtime}) or
        (endTime >= #{starttime} and endTime <= #{endtime}) or
        (startTime <= #{starttime} and endTime >= #{endtime}) or
        (startTime >= #{starttime} and endTime <= #{endtime}))


and isLoop = 0 and userId = #{userid}
order by year desc,month desc,day desc

    limit #{pageNum},#{pageSize}

mapper.update:
update draft


            eventName=#{eventName}


            startTime=#{startTime}


            endTime=#{endTime}


            address=#{address}


            level=#{level}


            remindTime=#{remindTime}


            remarks=#{remarks}


            person=#{person}


            repeatTime=#{repeatTime}


            day=#{day}


            month=#{month}


            year=#{year}


            type=#{type}


             isLoop=#{isLoop}


where eventId=#{eventId} and userId=#{userId}

mapper.select:
select * from singleevent
where
userId = #{userid}
and month = #{month}
and day = #{day}
and year = #{year}
and ((startTime > #{starttime} and endTime < #{endtime})
or
(startTime > #{starttime} and startTime < #{endtime})
or
(endTime > #{starttime} and endTime < #{endtime})
or
(startTime <= #{starttime} and endTime >= #{endtime}))

select

from singleevent


        userid = #{userid}


        eventId < #{eventid}


        and day = #{day}


        and month = #{month}


        and year = #{year}


        and isOverdue = #{isOverdue}


        and isLoop = 0


order by day asc ,startTime asc

select eventId,userId,eventName,person,day,month,year
from draft

    1 = 1

        and eventName like "%"#{eventname}"%"


        and type = #{type}


        and level = #{level}


        and year = #{year}


        and month = #{month}


        and day = #{day}


        and isOverdue = #{isOverdue}


        and ((startTime >= #{starttime} and startTime <= #{endtime}) or
        (endTime >= #{starttime} and endTime <= #{endtime}) or
        (startTime <= #{starttime} and endTime >= #{endtime}) or
        (startTime >= #{starttime} and endTime <= #{endtime}))


and isLoop = 0 and userId = #{userid}
order by year desc,month desc,day desc

    limit #{pageNum},#{pageSize}
