mapper.update:
update

     singleevent


     draft

 set isOverdue = 2 , eventId=#{now}
 where eventId = #{eventId} and userId=#{userId}

mapper.delete:
delete from singleevent
where
  eventId = #{eventid}
and
  userId = #{userid}

delete from draft
where
  eventId = #{eventId}
and
  userId = #{userId}

mapper.select:
select count(id) from singleevent


        userId = #{userid}


        and month = #{month}


        and day = #{day}


        and year = #{year}


and ((startTime > #{starttime} and endTime < #{endtime})
or
(startTime > #{starttime} and startTime < #{endtime})
or
(endTime > #{starttime} and endTime < #{endtime})
or
(startTime <= #{starttime} and endTime >= #{endtime}))

select

from singleevent


        userid = #{userid}


        and day = #{day}


        and month = #{month}


        and year = #{year}


        and isOverdue in (1,3)


        and isOverdue = #{isOverdue}


        and isOverdue <> 2

    and isLoop = 0


order by day asc ,startTime asc

