mapper.select:
select

from singleevent


        userid = #{userid}


        and day = #{day}


        and month = #{month}


        and year = #{year}

    and isOverdue <> 2

order by day asc ,startTime asc

select

from singleevent


        userid = #{userid}


        and day = #{day}


        and month = #{month}


        and year = #{year}

    and isOverdue <> 2

order by level asc

select

from singleevent


        userid = #{userid}


        and day = #{day}


        and month = #{month}


        and year = #{year}

    and isOverdue <> 2

order by level asc , startTime asc

select

from singleevent


        userid = #{userid}


        and month = #{month}


        and year = #{year}

    and isOverdue <> 2

order by day asc

mapper.update:
update singleevent set
    isOverdue = 2
where userId = #{userid} and year =#{year} and month=#{month} and day=#{day}

mapper.select:
select

from singleevent


        userid = #{userid}


        and day = #{day}


        and month = #{month}


        and year = #{year}

    and isOverdue <> 2

order by startTime asc

select day from singleevent where
userId = #{userid}
and month = #{month}
and year = #{year}
group by day
