mapper.select:
select * from store_goods where storeId=#{storeId}

    and goodsName like concat('%',#{goodsName},'%')


    and goodsTypeId = #{goodsTypeId}

limit #{pageIndex},#{pageSize}

select sg.id goodsId,sg.goodsPicture goodsPicture,sg.goodsName goodsName, sgt.type goodsType,sg.goodsUnit goodsUnit,sum(sgk.stockNum) goodsStock from store_goods sg,store_goodsstock sgk,store_goods_type sgt where sg.id = sgk.goodsId and sgt.id = sg.goodsTypeId and sg.storeId = #{storeId}
and (sg.goodsName like "%"#{goodsName}"%" or sg.goodsBrand like "%"#{goodsName}"%" or sg.goodsSpecifications like "%"#{goodsName}"%")
group by goodsId limit #{pageNum},#{pageSize}

select sg.id goodsId,sg.goodsPicture goodsPicture,sg.goodsName goodsName, sgt.type goodsType,sg.goodsUnit goodsUnit,sg.goodsPrice goodsPrice from store_goods sg,store_goods_type sgt where sgt.id = sg.goodsTypeId and sg.storeId = #{storeId}
and (sg.goodsName like "%"#{goodsName}"%" or sg.goodsBrand like "%"#{goodsName}"%" or sg.goodsSpecifications like "%"#{goodsName}"%")
group by goodsId limit #{pageNum},#{pageSize}
