mapper.select:
SELECT sg.*,sgs.`goodsPrice`,sgs.`stockNum` FROM store_goods sg
INNER JOIN store_goods_stock sgs
ON sg.id=sgs.`goodsId` WHERE sgs.storeId=#{storeId} AND sg.`goodsTypeId`=#{goodsTypeId}

    and sg.goodsName like concat('%',#{goodsName},'%')


    limit #{pageIndex},#{pageSize}

select sg.id goodsId,sg.goodsPicture goodsPicture,sg.goodsName goodsName, sgt.type goodsType,sg.goodsUnit goodsUnit,sum(sgk.stockNum) goodsStock from store_goods sg,store_goods_stock sgk,store_goods_type sgt where sg.id = sgk.goodsId and sgt.id = sg.goodsTypeId and sg.storeId = #{storeId}
and (sg.goodsName like "%"#{goodsName}"%" or sg.goodsBrand like "%"#{goodsName}"%" or sg.goodsSpecifications like "%"#{goodsName}"%")
group by sg.id limit #{pageNum},#{pageSize}

select sg.id goodsId,sg.goodsPicture goodsPicture,sg.goodsName goodsName, sgt.type goodsType,sg.goodsUnit goodsUnit,sgk.goodsPrice goodsPrice from store_goods sg,store_goods_type sgt,store_goods_stock sgk where sgt.id = sg.goodsTypeId and sg.storeId = #{storeId}
and (sg.goodsName like "%"#{goodsName}"%" or sg.goodsBrand like "%"#{goodsName}"%" or sg.goodsSpecifications like "%"#{goodsName}"%")
group by sg.id limit #{pageNum},#{pageSize}

select sg.id goodsId,sg.goodsPicture,sg.goodsName, sg.goodsUnit,sgs.stockNum goodsStock,(select type from store_goods_type sgt where sgt.id = sg.goodsTypeId) goodsType from store_goods sg,store_goods_stock sgs where sg.id = sgs.goodsId and sg.storeId = #{storeId}
and (sg.goodsName like "%"#{goodsName}"%" or sg.goodsBrand like "%"#{goodsName}"%" or sg.goodsSpecifications like "%"#{goodsName}"%")
group by sg.id limit #{pageNum},#{pageSize}

select sg.id goodsId,sg.goodsPicture,sg.goodsName, sg.goodsUnit,sgs.stockNum goodsStock,(select type from store_goods_type sgt where sgt.id = sg.goodsTypeId) goodsType from store_goods sg,store_goods_stock sgs where sg.id = sgs.goodsId and sg.storeId = #{getGoodsStockList.storeId} and sg.id not in (${corIds})
and (sg.goodsName like "%"#{getGoodsStockList.goodsName}"%" or sg.goodsBrand like "%"#{getGoodsStockList.goodsName}"%" or sg.goodsSpecifications like "%"#{getGoodsStockList.goodsName}"%")
limit #{getGoodsStockList.pageNum},#{getGoodsStockList.pageSize}
